const { itemType } = options;
const isAdmin = aras.isAdminUser();

if (!isAdmin || !itemType || itemType.name !== 'CUI_Application') {
  return { hidden: true };
}

const state = options.getState();
const { selectedRows } = state.grid;

return { disabled: selectedRows.length !== 1 }
